using MailKit;
using MailKit.Net.Smtp;
using MailKit.Security;
using Microsoft.Extensions.Options;
using MimeKit;
using SendMailApi.Settings;

namespace SendMailApi.Services
{
    public class MailService : IMailService
    {
        private readonly MailSettings _mailSettings;



        public MailService(IOptions<MailSettings> mailSettings)
        {
            _mailSettings = mailSettings.Value;
        }

        //public MailService(IMailSettings mailSettings)
        //{
        //    _mailSettings = mailSettings ?? throw new ArgumentNullException(nameof(mailSettings));
        //}

        public async Task SendEmailAsync(MailRequest mailRequest)
        {
            var email = new MimeMessage();
            email.Sender = MailboxAddress.Parse(_mailSettings.Mail);
            email.To.Add(MailboxAddress.Parse(mailRequest.ToEmail));
            email.Subject = mailRequest.Subject;
            var builder = new BodyBuilder();
            if (mailRequest.Attachments != null)
            {
                byte[] fileBytes;
                foreach (var file in mailRequest.Attachments)
                {
                    if (file.Length > 0)
                    {
                        using (var ms = new MemoryStream())
                        {
                            file.CopyTo(ms);
                            fileBytes = ms.ToArray();
                        }
                        builder.Attachments.Add(file.FileName, fileBytes, ContentType.Parse(file.ContentType));
                    }
                }
            }
            builder.HtmlBody = @"<!DOCTYPE html>
<html lang=""en"">

<head>
    <meta charset=""UTF-8"">
    <meta name=""viewport"" content=""width=device-width, initial-scale=1.0"">
    <title>Password</title>
    <style>
        body {
            display: flex;
            justify-content: center;
            align-items: center;
            min-height: 100vh;
            margin: 0;
            background-color: #f9f9f9;
        }

        .beyaz-kutu {
            background-color: white;
            width: 600px;
            height: 600px;
            border: 1px;
        }

        .metin {
            text-align: left;
            margin-left: 50px;
            padding-top: 40px;
            color: rgb(92, 89, 89);
            font-family: Calibri;
        }

        .thumb-info .thumb-info-title {
            transition: all 0.3s;
            background: rgb(47, 135, 64);
            color: #FFF;
            left: 0;
            letter-spacing: -.05em;
            position: absolute;
            z-index: 2;
            max-width: 90%;
            font-size: 17px;
            padding: 5px 21px 5px;
            bottom: 0%;
            font-weight: 400;
        }

        .thumb-info .thumb-info-inner {
            transition: all 0.3s;
            display: block;
        }
    </style>
</head>

<body>

    <div class=""beyaz-kutu"">
        <img src=""data:image/png;base64,""
            style=""margin-top: 50px;padding-left: 200px;"">

        <div class=""metin"">
            <h1 style=""text-align: center;"">Şifrenizi Sıfırlayın</h1>
            <p>Merhaba,</p>
            <p>Şifrenizi sıfırlama talebinize yanıt olarak bu e-postayı size gönderdik. Şifre oluştururken dikkat
                edilmesi kurallar:</p>
            <ul>
                <li>En az 6 karakterden oluşur.</li>
                <li>En az bir büyük harf, bir küçük harf ve rakam gibi karakterler içerir.</li>
            </ul>
            <br><br>
            <p>Şifrenizi sıfırlamak için lütfen aşağıdaki bağlantıya tıklayınız:</p>
            <br>


            <a href=""https://www.delphinhotel.com/"" target=""_blank"">
                <span class=""thumb-info-title ref-content"">
                    <span class=""thumb-info-inner"">Şifre
                        Yenile</span>
                </span>
            </a>

            <script>
                document.getElementById('sifreYenileButton').addEventListener('click', function () {
                    var callbackUrl = '[ForgotMailLink]';

                    window.open(callbackUrl, '_blank');
                });
            </script>
            <br><br><br><br>
            <p style=""font-style: italic;font-size: small;"">Parola değişikliği talebinde bulunmadıysanız lütfen bu
                e-postayı dikkate almayın.</p>
        </div>
    </div>
</body>

</html>";
            email.Body = builder.ToMessageBody();
            using var smtp = new SmtpClient();
            smtp.Connect(_mailSettings.Host, _mailSettings.Port, SecureSocketOptions.StartTls);
            smtp.Authenticate(_mailSettings.Mail, _mailSettings.Password);
            await smtp.SendAsync(email);
            smtp.Disconnect(true);
        }
    }
}
